#!/usr/bin/env python# coding:utf-8import reimport numpy as npfrom jpod.input_output import (IOFormatSelector, Dataset)# Input from header.pywith open('./jpod-data/header.py', 'r') as a:    for line in a.readlines():        A = re.match(r'x1 = (.*$)', line, re.M | re.I)        if A:            x1 = "{:.7}".format(A.group(1))        B = re.match(r'x2 = (.*$)', line, re.M | re.I)        if B:            x2 = "{:.7}".format(B.group(1))X1 = float(x1)X2 = float(x2)# FunctionF = - np.sin(X1) * (np.power(np.sin(X1 * X1 / np.pi), 20.)) - \    np.sin(X2) * (np.power(np.sin(2 * X2 * X2 / np.pi), 20.))# Outputdata = np.array(F)names = ["F"]io = IOFormatSelector('fmt_tp_fortran')dataset = Dataset(names=names, shape=[1, 1, 1], data=data)io.write('./cfd-output-data/function.dat', dataset)